import './style.scss';
import React from 'react';
import {withRouter} from 'next/router';
import axios from '../../utils'; 
import ArticleV from './ArticleV'; 
class ArticlePage extends React.Component{
    constructor(props){
        super(props);
        this.state = {form:{},
            modal:false,
            articles:[],
            article:null,  
            showSucess: false,
            sucess: null,
            search: null,
            params:{ page: 1, size: 20 },
            upload: null 
        };
         
        this.fetchArticle = this.fetchArticle.bind(this); 
    }   
    fetchArticle(){
        let {articles,params,search} = this.state; 
        try{
            axios.get('/article',{params:{search}})
             .then(res => {  
                if(res.data){ 
                    articles = res.data.data || []
                    this.setState({ articles }); 
                    return articles 
                }
             })
         }catch(err){ 
             throw err 
         }
    } 
    componentDidMount(){
        this.fetchArticle();
    }
    render(){
        const {articles, modal, article, handleGetId,sucess,showSucess ,upload} = this.state; 
        return(
            <div className="row">
                <ArticleV 
                    articles={articles}
                    handleGetIdArticle={this.handleGetIdArticle}  
                /> 
            </div>
        )
    }
} 
export default withRouter(ArticlePage)
