import React, { Component } from 'react';
import { EditorState, convertToRaw, ContentState } from 'draft-js';
import { Editor } from 'react-draft-wysiwyg';
import draftToHtml from 'draftjs-to-html';
import htmlToDraft from 'html-to-draftjs';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import './styles.scss';

export default class EditorConvertToHTML extends Component {
  constructor(props) {
    super(props);
    const html = '<p></p>';
    const contentBlock = htmlToDraft(html);
    if (contentBlock) {
      const contentState = ContentState.createFromBlockArray(contentBlock.contentBlocks);
      const editorState = EditorState.createWithContent(contentState);
      this.state = {
        editorState,
      }; 
    }
  }
  
  onEditorStateChange = (editorState) => {
    let {editorStateView} = this.props;
      editorStateView = draftToHtml(convertToRaw(editorState.getCurrentContent()));
    this.setState({editorState});
  };

  render() {
    const { editorState } = this.state;
    const { handleChangeEditor, editorStateView } = this.props ;
    let result = draftToHtml(convertToRaw(editorState.getCurrentContent())) ;
    // console.log(handleChangeEditor(editorState),'============>:')
    return (
      <div>
        <Editor
          editorState = {editorState}
          wrapperClassName="demo-wrapper"
          editorClassName="demo-editor"
          onEditorStateChange={handleChangeEditor}
          onChange ={handleChangeEditor()}
        />
        <textarea
          className="text-html"
          disabled
          value={result}
          name="editor"
          // onChange={}
        />
      </div>
    );
  }
}
