import '../styles.scss'
import React from 'react';
import {withRouter} from 'next/router';
import {URL_ROOT} from '../../../config'
import Link from 'next/link';
import axios from '../../../utils'; 

class DetailPage extends React.Component{
    constructor(){
        super();
        this.state = {article:null};  
    }
    fetchArticle(){
        let {article} = this.state; 
        const {query} = this.props.router; 
        try{
            axios.get(`/article/${query.slug}`)
            .then(res => {  
                if(res.data){
                    article = res.data.data ;
                    
                    this.setState({ article });  
                    return article 
                }
            })
        }catch(err){
            throw err
        }
    }
    componentDidMount(){
        this.fetchArticle();
    }
    componentDidUpdate(props){
        const {query} = this.props.router; 
        const oldeQuery = props.router.query;
        if(query && query.slug && oldeQuery.slug && query.slug  !== oldeQuery.slug) { 
            console.log(query,'===================>/asdf')
            this.fetchArticle();
        }
    }
    render(){ 
        const {article} = this.state; 
        return(
            <div className="box">  
                <div className="media">
                    <div className="col-md-12">  
                    {
                        article ? 
                        <div>
                            <h4><span>{ article.title }</span> </h4>
                            <img src={URL_ROOT + article.background} width="100%"/>  
                            <div dangerouslySetInnerHTML = {{ __html : article.content }} />
                        </div>
                         : null
                    }
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter(DetailPage)
