import React from 'react';
import {withRouter} from 'next/router';
import axios from '../../utils'; 
import ModalV from '../components/modal';
import CommentAdd from './commentAdd';
import CommentV from './commentV';

class Comment extends React.Component{
    constructor(props){
        super(props);
        this.state =  {
            modal:false,
            comments : []
        }
        this.handleModal = this.handleModal.bind(this);
        this.fetchComment = this.fetchComment.bind(this);
    }

    handleModal(param){
        let {modal} = this.state;
        modal = param;
        this.setState({modal});
    }
    fetchComment(){
        let {comments} = this.state;
        axios.get('/comment')
        .then(res =>{
            const {data} = res.data;
            comments = data;
            this.setState({comments});
            // console.log(comments,'=============>fetch cmo')
        })
    }
    deleteConfirm(param){
        let {modleShow} = this.state;
        modleShow = param; 
        this.setState({modleShow});
         
    }
    handleGetIdArticle(param){
        let {handleGetId} = this.state;
        handleGetId = param;
        this.setState({handleGetId});
    }
    componentDidMount(){ 
        this.fetchComment()
    }
    render(){ 
        const {modal,comments} = this.state;
        return(
            <div className="row"> 
                <div className="col-md-12 text-right">
                    <button type="button" onClick={()=>{this.handleModal(true) }} className="btn btn-primary col-md-1">them</button>
                </div> 
                <CommentV comments = {comments} /> 
                <div className="col-md-12">
                    { modal ?
                    <CommentAdd /> : ''
                    }
                </div>
            </div>
        )
    }
} 
export default withRouter(Comment)
