import React from 'react';
import axios from 'axios';
import AddUser from './add';

export default class User extends React.Component{
    constructor(props){
        super(props);
        this.state = {form:{}, modal:false,users:[],user:null};
        
        this.handleModal = this.handleModal.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fetchUser = this.fetchUser.bind(this);
        this.handleEdit = this.handleEdit.bind(this);
    }
    handleChange(even){
        let {form} = this.state;
        const name = even.target.name;
        const value = even.target.value;
        form[name] = value;
        this.setState({ form });
    }
    handleSubmit(event){
        event.preventDefault();
        let {form} = this.state;
        // console.log(form,'==================??data')
        try{
            axios.post('http://localhost:3333/user/add',{form})
            .then(res=>{ 
                if(res.data){
                    this.fetchUser();
                }
            })
        }catch(err){
            throw err
        }
    }
    handleModal(param){
        let {modal} = this.state;
        modal = param;
        this.setState({modal});
    }
    fetchUser(){
        let {users} = this.state;
        try{
            axios.get('http://localhost:3333/user')
             .then(res => { 
                if(res.data){
                    users = res.data.data
                    this.setState({ users }); 
                    return users 
                }
             })
         }catch(err){ 
             throw err 
         }
    }
    handleEdit(param){
        let {user} = this.state;
        user = param;
        this.setState({user});
    }
    componentDidMount(){
        this.fetchUser();
    }
    render(){
        const {users,modal} = this.state; 
        return(
            <div className="row"> 
                <div className="col-md-12 text-right">
                    <button type="button" onClick={()=>{this.handleModal(true);this.handleEdit(false)}} className="btn btn-primary col-md-1">them</button>
                </div>
                {modal ? '' :
                    <div  className="col-md-12">
                        <table className="table">
                            <thead>
                                <tr> 
                                <th scope="col">user</th>
                                <th scope="col">email</th> 
                                </tr>
                            </thead>
                            <tbody>
                                {users && users.length > 0 ? users.map((item,key)=>{
                                    return( 
                                        <tr key={key}> 
                                            <td>{item.name}</td> 
                                            <td>{item.email}</td> 
                                            <td>
                                                <div className="btn-group">
                                                    <button type="submit" className="btn btn-primary" onClick={()=>{this.handleModal(true);this.handleEdit(false)}} ><span>edit</span></button>
                                                    <button type="button" className="btn btn-secondary"><span>cancel</span></button>
                                                </div>
                                            </td>
                                        </tr>
                                    )
                                }) : []}  
                            </tbody>
                        </table>
                    </div>
                }
                <div className="col-md-12">
                    {modal && <AddUser
                        handleChange={this.handleChange}
                        handleSubmit={this.handleSubmit}
                        handleModal={this.handleModal}
                        fetchUser = {this.fetchUser}
                    />} 
                </div>
            </div>
        )
    }
}
