import React from 'react'

export default class AddUser extends React.Component{ 
    constructor(props){
        super(props);
        this.state = {initialValue:this.props.article}; 
    }

    render(){
        const {handleSubmit,handleChange,handleModal,handleUpload, upload, showImage} = this.props; 
        let {initialValue} = this.state; 
        const options = [
            {  key: 1,  value: '' },
            {  key: 2, text: 'new-one', value: '1' },
            {  key: 3, text: 'new-two', value: '2' }
        ]
        return(
            <div className="col-md-12">
                <form  method='post' encType="multipart/form-data" onSubmit={handleSubmit} >
                    <div className="form-group">
                        <label  >name</label>
                        <input name="name" className="form-control" onChange={handleChange} defaultValue={initialValue.title}  /> 
                    </div> 
                    <div className="form-group">
                        <label >Content:</label>
                        <textarea name="content" defaultValue={initialValue.des} className="form-control" rows="5"  onChange={handleChange}>
                        </textarea>
                    </div>
                    <div className="form-group">
                        <label >type</label>
                        <select className="form-control"name="option" onChange={handleChange}>
                        {
                            options && options.map((item, key)=>{ 
                                return(
                                    <option value={item.value} key={key}> {item.text} </option>
                                )
                            })
                        }
                        </select>
                    </div>
                    <div className="custom-file">
                        <input type="file" className="custom-file-input"  onChange={handleUpload} />
                        <label className="custom-file-label" >choose background</label>
                    </div>
                    <div className="btn-group">
                        <button type="submit" className="btn btn-primary" onClick={()=>setTimeout(()=>{handleModal(false)},600 )}>Luu va dong</button>
                        <button type="button" onClick={()=>handleModal(false)}  className="btn btn-secondary">cancel</button>
                    </div>
                </form>
            </div>
        )
    }
}
