import React from 'react';
import {withRouter} from 'next/router';

class LoginPage extends React.Component{
    constructor(props){
        super(props);
        this.state = {form:{}};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event){
        let {form} = this.state;
        const value = event.target.value;
        const name = event.target.name;
        form[name] = value;
        
        this.setState({form});
        console.log(this.state.form,'=============??')
    }
    handleSubmit(event){
        event.preventDefault();
        const {form} = this.state;
        try{  
                axios.post("http://localhost:3333/login",{form})
                .then(res=>{
                    // let param = res.data.message;
                    if(res.data){
                        console.log(res.data,'===================??res.dat')
                        // this.fetchUser();
                        // this.opentSucces(param);
                        // this.handleSucces(true)
                    }
                })
            
        }catch(err){
            throw err
        }
    }
    render(){

        return(
            <div>
                 <form  method='post' encType="multipart/form-data" onSubmit={this.handleSubmit} > 
                    <div className="form-group">
                        <label  >Email </label>
                        <input type="email" name="email" className="form-control" onChange={this.handleChange}  /> 
                    </div>
                    <div className="form-group">
                        <label  >Password </label>
                        <input name="password" type="text" onChange={this.handleChange} className="form-control"   /> 
                    </div>
                    <div className="btn-group">
                        <button type="submit" className="btn btn-primary">Đăng nhập</button> 
                    </div>
                </form>
            </div>
        )
    }
}
export default withRouter(LoginPage);
