import React from 'react';
import {withRouter} from 'next/router';
import axios from 'axios';
import Auth from '../../localStorege';
import ModalV from '../components/modal';
class LoginPage extends React.Component{
    constructor(props){
        super(props);
        this.state = {form:{},sucess:false};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.opentSuccess = this.opentSuccess.bind(this);
        this.handleSucces = this.handleSucces.bind(this);

    }
    handleChange(event){
        let {form} = this.state;
        const value = event.target.value;
        const name = event.target.name;
        form[name] = value; 
        this.setState({form});
        console.log(this.state.form,'=============??')
    }

    handleSucces(param){
        let {showSucess} = this.state;
        showSucess = param;
        this.setState({showSucess}); 
        let close = false;
        setTimeout(()=>{
            showSucess = close;
            this.setState({showSucess}); 
        },2500)
    }

    opentSuccess(param){
        let {sucess} = this.state;
        sucess = param;
        this.setState({sucess}); 
    }
    handleSubmit(event){
        event.preventDefault();
        const {form} = this.state;
        try{  
                axios.post("http://localhost:3333/login",{form})
                .then(res=>{
                    // let param = res.data.message;
                    if(res.data){
                        const token = res.data
                        console.log(token.data,'===================??res.dat')
                        Auth.authenticateUser(token.data)
                    }
                })
            
        }catch(err){
            throw err
        }
    }
    fetchUser(){
         
    }
    render(){
        // const token = Auth.getToken();
        // if(token){

           
        // }
        return(
            <div className="row">
                <div className="col-md-6"> 
                    <ModalV opentSuccess ={this.opentSuccess} handleSucces />
                 <form  method='post' encType="multipart/form-data" onSubmit={this.handleSubmit} > 
                    <div className="form-group">
                        <label  >Email </label>
                        <input type="email" name="email" className="form-control" onChange={this.handleChange}  /> 
                    </div>
                    <div className="form-group">
                        <label  >Password </label>
                        <input name="password" type="text" onChange={this.handleChange} className="form-control"   /> 
                    </div>
                    <div className="btn-group">
                        <button type="submit" className="btn btn-primary">Đăng nhập</button> 
                    </div>
                </form>
                </div>
            </div>
        )
    }
}
export default withRouter(LoginPage);
