import './style.scss'; 
import React from 'react';
import axios from '../../../utils';

class Sider extends React.Component{
    constructor (props){
        super(props);
        this.state = { 
            categories: [] 
        }
    }
    fetchCategories(){
        try{
            axios.get('/category')
            .then(result =>{
                const {data} = result.data;
                this.setState({categories:data});
            })
        }
        catch(err){
            throw err
        }
    }
    componentDidMount(){
        this.fetchCategories()
    }
