import './styles.scss'
import React from 'react';
import {withRouter} from 'next/router';
import {URL_ROOT} from '../../config'
import Link from 'next/link';
import Moment from 'moment'
const ArticelV = (props)=>{
    const {articles} = props;
   
    let aa = []; 
    articles && articles.length > 0 ? articles.map((item,key)=> {
        const moonLanding = new Date(item.created_date);
        const months = ["January","February","March","April","May","June","July","August","September","October","November","December"];
        const getMonth = months[moonLanding.getMonth()] ;
        
      const format =   Moment(item.created_date).format("MM-YYYY")
        if(aa.lastIndexOf(format) == -1){
            aa.push(format);
            // console.log(aa,'======sdf=======>a',)
            return aa
        }
    }) : []
    console.log(aa,'========1=====>a')
    
    return(
        <div className="box"> 
            {
                aa && aa.length > 0 ? aa.map((item,key)=> {
                     console.log(item,'=====>?')
                    return(
                        <div  key={key} className="row">
                            <p>
                                <Link href={'/chi-tiet/[slug]'} as={`/chi-tiet/${item._id}`} >
                                    <a>
                                        <p><span>{item}</span></p>
                                    </a>
                                </Link>
                            </p>
                        </div>
                    )
                }) : <p>not find products</p>
            }
        </div>
    )
}

export default ArticelV
