import React from 'react';
import axios from 'axios';
import AddUser from './add';

export default class User extends React.Component{
    constructor(props){
        super(props);
        this.state = {form:{}, modal:false};
        
        this.handleModal = this.handleModal.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(even){
        let {form} = this.state;
        const name = even.target.name;
        const value = even.target.value;
        form[name] = value;
        this.setState({ form });
    }
    handleSubmit(event){
        event.preventDefault();
        let {form} = this.state;
        // console.log(form,'==================??data')
        axios.post('http://localhost:3333/user/add',{form})
        .then(res => {
            console.log(res);
            console.log(res.data);
          })
    } 
    handleModal(param){
        let {modal} = this.state;
        modal = param;
        this.setState({modal});
    }

    componentDidMount(){
        // let {form} = this.state;
        // console.log(form,'==================fech??') 
       const get =  axios.get('http://localhost:3333/user')
        .then(res => {
            console.log(res,'======================?data res send');
        })
        console.log(get,'======================?data res send');
    }

    render(){
        const {modal} = this.state;
        return(
            <div className="row"> 
                <div className="col-md-12 text-right">
                    <button type="button" onClick={()=>this.handleModal(true)} className="btn btn-primary col-md-1">them</button>
                </div>
                <div className="col-md-12">
                    {modal && <AddUser handleChange={this.handleChange} handleSubmit={this.handleSubmit} />} 
                </div>
            </div>
        )
    }
}
