import React from 'react';
import axios from 'axios';
import AddUser from './add';

export default class User extends React.Component{
    constructor(props){
        super(props);
        this.state = {form:{}, modal:false,users:[]};
        
        this.handleModal = this.handleModal.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fetchUser = this.fetchUser.bind(this);
    }
    handleChange(even){
        let {form} = this.state;
        const name = even.target.name;
        const value = even.target.value;
        form[name] = value;
        this.setState({ form });
    }
    handleSubmit(event){
        event.preventDefault();
        let {form} = this.state;
        // console.log(form,'==================??data')
        try{
            axios.post('http://localhost:3333/user/add',{form}) 
        }catch(err){
            throw err
        }
    } 
    handleModal(param){
        let {modal} = this.state;
        modal = param;
        this.setState({modal});
    }
    fetchUser(){
        let {users} = this.state;
        try{
            axios.get('http://localhost:3333/user')
             .then(res => {
                // console.log(res.data,'=======================???.data')
                users = res.data.data
                this.setState({ users });
                 return users
             }) 
         }catch(err){ 
             throw err 
         }
    }
    componentDidMount(){
        this.fetchUser();
    }

    render(){
        const {users,modal} = this.state;
        console.log(users,'========================?users')
        return(
            <div className="row"> 
                <div className="col-md-12 text-right">
                    <button type="button" onClick={()=>this.handleModal(true)} className="btn btn-primary col-md-1">them</button>
                </div>
                <div className="col-md-12">
                <table className="table">
                    <thead>
                        <tr> 
                        <th scope="col">user</th>
                        <th scope="col">email</th> 
                        </tr>
                    </thead>
                    <tbody>
                        
                        <tr>
                            <th scope="row">1</th>
                            <td>Mark</td>
                            <td>Otto</td>
                            <td>@mdo</td>
                        </tr>
                        
                    </tbody>
                </table>
                </div>
                <div className="col-md-12">
                    {modal && <AddUser handleChange={this.handleChange} handleSubmit={this.handleSubmit} />} 
                </div>
            </div>
        )
    }
}
