import React from 'react';
import axios from 'axios';
import AddUser from './add';
import {Button, Modal} from 'react-bootstrap';
import Moment from 'moment';

export default class User extends React.Component{
    constructor(props){
        super(props);
        this.state = {form:{},
            modal:false,
            users:[],
            user:null,
            modleShow:false,
            getUserId : null,
            showSucess: false,
            sucess: null,
        };
        
        this.handleModal = this.handleModal.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fetchUser = this.fetchUser.bind(this);
        this.handleEdit = this.handleEdit.bind(this);
        this.handleRemove = this.handleRemove.bind(this);
        this.deleteConfirm = this.deleteConfirm.bind(this);
        this.handleGetIdUser = this.handleGetIdUser.bind(this);
        this.opentSucces = this.opentSucces.bind(this);
        this.handleSucces = this.handleSucces.bind(this);
    }
    handleChange(even){
        let {form} = this.state;
        const name = even.target.name;
        const value = even.target.value; 
        form[name] = value;
        this.setState({ form });
    }
    handleSubmit(event){
        event.preventDefault();
        let {form,user} = this.state; 
        try{
            if(user._id){
                const id = user._id;
                axios.put(`http://localhost:3333/user/edit/${id}`,{form})
                .then(res=>{ 
                    if(res.data){
                        this.fetchUser();
                    }
                })
            }else{
                axios.post('http://localhost:3333/user/add',{form})
                .then(res=>{ 
                    let param = res.data.message;
                    if(res.data){ 
                        this.fetchUser();
                        this.opentSucces(param);
                        this.handleSucces(true)
                    }
                }) 
            }
        }catch(err){
            throw err
        }
    }
    handleModal(param){
        let {modal} = this.state;
        modal = param;
        this.setState({modal});
    }
    //==================================>Email existed
    opentSucces(param){
        let {sucess} = this.state;
        sucess = param;
        this.setState({sucess}); 
    }
    handleSucces(param){
        let {showSucess} = this.state;
        showSucess = param;
        this.setState({showSucess}); 
        let close = false;
        setTimeout(()=>{
            showSucess = close;
            this.setState({showSucess}); 
        },2000)
    }
    //==================================>//
    fetchUser(){
        let {users} = this.state;
        try{
            axios.get('http://localhost:3333/user')
             .then(res => { 
                if(res.data){
                    users = res.data.data
                    this.setState({ users }); 
                    return users 
                }
             })
         }catch(err){ 
             throw err 
         }
    }
    
    handleEdit(param){
        let {user} = this.state;
        user = param;
        this.setState({user});
    }
    deleteConfirm(param){
        let {modleShow} = this.state;
        modleShow = param; 
        this.setState({modleShow});
         
    }
    handleGetIdUser(param){
        let {getUserId} = this.state;
        getUserId = param;
        this.setState({getUserId});
    }
    handleRemove(param){
        const id = param._id; 
        this.deleteConfirm(true); 
        try{
            axios.delete(`http://localhost:3333/user/delete/${id}`)
             .then(res => { 
                if(res.data){
                    this.fetchUser();
                }
             })
         }catch(err){ 
             throw err 
         }
    }
    componentDidMount(){
        this.fetchUser();
    }
    render(){
        const {users, modal, user, modleShow, getUserId,sucess,showSucess} = this.state; 
        return(
            <div className="row"> 
                <div className="col-md-12 text-right">
                    <button type="button" onClick={()=>{this.handleModal(true);this.handleEdit(false)}} className="btn btn-primary col-md-1">them</button>
                </div>
                {
                    modal ? '' :
                    <div  className="col-md-12">
                        <table className="col-md-12">
                            <thead>
                                <tr> 
                                    <th scope="col">user</th>
                                    <th scope="col">email</th> 
                                    <th scope="col">created-date</th> 
                                    <th scope="col">update-date</th> 
                                </tr>
                            </thead>
                            <tbody>
                                {users && users.length > 0 ? users.map((item,key)=>{ 
                                    return( 
                                        <tr key={key}>
                                            <td>{item.name}</td> 
                                            <td>{item.email}</td> 
                                            <td>{item.created_date? Moment(item.created_date).format("DD-MM-YYYY") : 'N/A'}</td> 
                                            <td>{item.created_update?Moment(item.created_update).format("DD-MM-YYYY") : 'N/A'}</td> 
                                            <td>
                                                <div className="btn-group text-right">
                                                    <button type="submit" className="btn btn-primary" onClick={()=>{this.handleModal(true);this.handleEdit(item)}} ><span>edit</span></button>
                                                    <button type="button" className="btn btn-danger"onClick={()=>{this.deleteConfirm(true); this.handleGetIdUser(item)}}><span>delete</span></button>
                                                    <div> 
                                                    </div>
                                                </div>
                                            </td>
                                        </tr>
                                    )
                                }) : []}
                            </tbody>
                        </table> 
                    </div>
                }
                { 
                    getUserId ?
                        <Modal show={modleShow} onHide ={this.deleteConfirm} > 
                            <Modal.Body>Are you sure!</Modal.Body>
                            <Modal.Footer>
                            <Button variant="secondary" onClick ={()=>this.deleteConfirm(false)}  >
                                Close
                            </Button>
                            <Button variant="primary" onClick ={()=>{this.handleRemove(getUserId);this.deleteConfirm(false)}}>
                                delete
                            </Button>
                            </Modal.Footer>
                        </Modal> :''
                }
                { 
                    sucess ?
                        <Modal show={showSucess} size ="smal" onHide ={this.handleSucces}> 
                            <Modal.Body>{sucess}</Modal.Body> 
                        </Modal> :''
                }
                <div className="col-md-12">
                    {modal && <AddUser
                        handleChange={this.handleChange}
                        handleSubmit={this.handleSubmit}
                        handleModal={this.handleModal}
                        fetchUser = {this.fetchUser}
                        user = {user}
                    />} 
                </div>
            </div>
        )
    }
}
