import React from 'react'

export default class AddUser extends React.Component{ 
    constructor(props){
        super(props);
        // this.state = {initialValue:this.props.article}; 
    }

    render(){
        // const { } = this.props;  
        
        return(
            <div className="col-md-12">
                <form  method='post' encType="multipart/form-data"  >
                    <div className="form-group">
                        <label >message</label>
                        <textarea name="content"  className="form-control" rows="5"  >
                        
                        </textarea>
                    </div>
                    <div className="form-group">
                        <label >reply</label>
                        <textarea name="content"  className="form-control" rows="5"  > 
                        </textarea>
                    </div> 
                    <div className="btn-group">
                        <button type="submit" className="btn btn-primary" onClick={()=>setTimeout(()=>{handleModal(false)},600 )}>send</button>
                        <button type="button" onClick={()=>handleModal(false)}  className="btn btn-secondary">cancel</button>
                    </div>
                </form>
            </div>
        )
    }
}
