import React, { Component } from 'react';
import { EditorState, convertToRaw, ContentState } from 'draft-js';
import { Editor } from 'react-draft-wysiwyg';
import draftToHtml from 'draftjs-to-html';
import htmlToDraft from 'html-to-draftjs';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import './styles.scss';

export default class EditorConvertToHTML extends Component {
  constructor(props) {
    super(props);

  }

  onEditorStateChange = (even) => {
   
    // let ss = draftToHtml(convertToRaw(editorState.getCurrentContent()))
    const {editorState} = this.props;
    console.log(editorState,'=============<1>')
    this.setState({
      even,
    });
  };

  render() {
    // const { editorState } = this.state;
    const { handleChangeEditor,editorState } = this.props ;
    let result = draftToHtml(convertToRaw(editorState.getCurrentContent())) ;
    console.log(editorState,'============>:>')
    return (
      <div>
        <Editor
          editorState={editorState}
          wrapperClassName="demo-wrapper"
          editorClassName="demo-editor"
          onEditorStateChange={this.onEditorStateChange}
        />
        <textarea
          className="text-html"
          disabled
          value={result}
          name="editor"
          // onChange={handleChangeEditor(result)}
        />
      </div>
    );
  }
}
