import './style.scss';
import React from 'react';
import {withRouter} from 'next/router';
import { EditorState } from 'draft-js';
// import { Editor } from 'react-draft-wysiwyg';
import axios from '../../utils';
import AddArticleV from './addArticle';
import ArticleV from './ArticleV';
import ModalV from '../components/modal'; 
class Article extends React.Component{
    constructor(props){
        super(props);
        this.state = {form:{},
            modal:false,
            articles:[],
            article:null,
            handleGetId: null,
            modleShow:false,
            showSucess: false,
            sucess: null,
            categories: [],
            search: null,
            params:{ page: 1, size: 20 },
            upload: null,
            showImage: null,
            editorState: EditorState.createEmpty(),
        };
        
        this.handleModal = this.handleModal.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fetchArticle = this.fetchArticle.bind(this);
        this.handleEdit = this.handleEdit.bind(this);
        this.handleRemove = this.handleRemove.bind(this);
        this.deleteConfirm = this.deleteConfirm.bind(this);
        this.handleGetIdArticle = this.handleGetIdArticle.bind(this);
        this.opentSucces = this.opentSucces.bind(this);
        this.handleSucces = this.handleSucces.bind(this);
        this.getsearch = this.getsearch.bind(this);
        this.handleUpload = this.handleUpload.bind(this);
        this.fetchCategories = this.fetchCategories.bind(this);
        this.handleChangeEditor = this.handleChangeEditor.bind(this)
    }
    handleChange(even){
        let {form} = this.state;
        const name = even.target.name;
        const value = even.target.value; 
        form[name] = value; 
        this.setState({ form }); 
        console.log(form,'===>:')
    }
    handleChangeEditor(editorState){
        this.setState({ 
            editorState
           });
          console.log(this.state.editorState.getCurrentContent().getPlainText(),'=====>oke')
    }
    handleUpload(e){
        let {upload} = this.state;
        upload = e.target.files[0] ;
        this.setState({ upload });
    }
    getsearch(even){
        let {search} = this.state;
        const value = even.target.value; 
        search = value;
        this.setState({search}); 
        this.fetchArticle()
    }
    handleSubmit(event){
        event.preventDefault(); 
        let formData = new FormData();
        let {form,article,upload} = this.state; 
        formData.append('file', upload); 
        formData.append('title', form.title); 
        formData.append('content', form.content); 
        formData.append('type', form.option);
        formData.append('high', form.high);  
        if(article._id){
            const id = article._id;
            axios.put(`/article/edit/${id}`,{form})
            .then(res=>{
                let param = res.data.message;
                if(res.data){
                    this.fetchArticle();
                    this.opentSucces(param);
                    this.handleSucces(true)
                }
            })
        }else{
            axios.post('/article/add',formData,{
                headers: {  'Content-Type': 'multipart/form-data' }
            })
            .then(res=>{ 
                let param = res.data.message; 
                if(res.data){
                    this.fetchArticle();
                    this.opentSucces(param);
                    this.handleSucces(true)
                }
            })
        } 
    } 
    handleModal(param){
        let {modal} = this.state;
        modal = param;
        this.setState({modal});
    }
    //==================================>Email existed
    opentSucces(param){
        let {sucess} = this.state;
        sucess = param;
        this.setState({sucess}); 
    }
    handleSucces(param){
        let {showSucess} = this.state;
        showSucess = param;
        this.setState({showSucess}); 
        let close = false;
        setTimeout(()=>{
            showSucess = close;
            this.setState({showSucess}); 
        },2500)
    }
    //==================================>//
    fetchArticle(){
        let {articles,params,search} = this.state; 
        try{
            axios.get('/article',{params:{search}})
             .then(res => { 
                let {showImage} = this.state;
                if(res.data){ 
                    articles = res.data.data || []
                    this.setState({ articles }); 
                    this.fetchCategories(); 
                    return articles 
                }
             })
         }catch(err){ 
             throw err 
         }
    }
    
    handleEdit(param){
        let {article} = this.state;
        article = param;
        this.setState({article});
    }
    deleteConfirm(param){
        let {modleShow} = this.state;
        modleShow = param; 
        this.setState({modleShow});
         
    }
    handleGetIdArticle(param){
        let {handleGetId} = this.state;
        handleGetId = param;
        this.setState({handleGetId});
    }
    handleRemove(param){
        const id = param; 
        this.deleteConfirm(true); 
        try{
            axios.delete(`/article/delete/${id}`)
             .then(res => { 
                if(res.data){
                    this.fetchArticle();
                }
             })
         }catch(err){ 
             throw err 
         }
    }
    fetchCategories(){
        try{
            axios.get('/category')
             .then(res => { 
                if(res.data){
                    let {categories} = this.state;
                    const {data} = res.data;
                    categories = data;
                    this.setState({categories}); 
                }
             })
         }catch(err){ 
             throw err 
         }
    }
    componentDidMount(){
        this.fetchArticle(); 
    }
    render(){
        const {editorState,categories,articles, modal, article, modleShow, handleGetId,sucess,showSucess ,upload, showImage,fetchCategories} = this.state; 
        return(
            <div className="row"> 
                <div className="col-md-12 text-right">
                    <button type="button" onClick={()=>{this.handleModal(true);this.handleEdit(false)}} className="btn btn-primary col-md-1">them</button>
                </div>
                { modal ? '' :
                    <div className="col-md-12 box-form"> 
                        <form >
                            <div className="form-row">
                                <div className="col-7">
                                    <input type="text"  className="form-control" placeholder="nhập từ cần tìm kiếm" onChange={this.getsearch} />
                                </div> 
                            </div>
                        </form>  
                    </div>
                }
                <ArticleV 
                    modal={modal} articles={articles}
                    handleModal={this.handleModal} deleteConfirm={this.deleteConfirm} handleEdit={this.handleEdit}
                    handleGetIdArticle={this.handleGetIdArticle} 
                    showImage ={showImage}
                    fetchCategories = {this.fetchCategories}
                    categories = {categories}
                /> 
                <ModalV
                    modleShow={modleShow} handleGetId={handleGetId} sucess={sucess} showSucess={showSucess} 
                    deleteConfirm={this.deleteConfirm} handleRemove ={this.handleRemove} handleSucces={this.handleSucces}
                  />
                <div className="col-md-12">
                    {
                        modal && <AddArticleV
                            handleChange={this.handleChange}
                            handleSubmit={this.handleSubmit}
                            handleModal={this.handleModal}
                            fetchArticle = {this.fetchArticle}
                            article = {article}
                            upload ={upload}
                            handleUpload ={this.handleUpload}
                            categories = {categories}
                            handleChangeEditor = {this.handleChangeEditor}
                            editorState={editorState}
                        />
                    }
                </div>
            </div>
        )
    }
} 
export default withRouter(Article)
