import '../styles.scss'
import React from 'react';
import {withRouter} from 'next/router';
import {URL_ROOT} from '../../../config'
import Link from 'next/link';
import axios from '../../../utils'; 

class DetailPage extends React.Component{
    constructor(props){
        super(props);
        this.state = {article:null, articles:[]};
        
    }
    fetchArticle(){
        let {articles} = this.state; 
        const {query} = this.props.router; 
        console.log(query,'========>query')
        try{
            axios.get(`/category/${query.slug}`)
            .then(res => {
                if(res.data){
                    articles = res.data.data ;
                    this.setState({ articles }); 
                }
            })
        }catch(err){
            throw err
        }
    }
    componentDidMount(){
        this.fetchArticle();
    }
    render(){
        const {articles} = this.state;
        console.log(articles,'=============1>?')
        return(
            <div className="box">  
                <div className="media">
                    <div className="col-md-12">  
                    <p>sdf</p>
                  {
                      articles && articles.length > 0 ? articles.map((item,key)=>{
                          return(
                            <div  key={key} className="col-md-4">
                                <div >
                                    <Link href={'/artile/[slug]'} as={`/artile/${item._id}`} >
                                        <a >
                                            <img src={URL_ROOT + item.background} width="100%"/>
                                        </a>
                                    </Link>
                                </div>
                                <div className="">
                                    <h4 className="mt-0"><span>{item.tile}</span> </h4>
                                    {/* <p>{item.content}</p> */}
                                </div>
                            </div>
                          )
                      }) : ''
                  }
                        
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter(DetailPage)
