import React from 'react'

export default class AddUser extends React.Component{ 
    constructor(props){
        super(props);
        this.state = {initialValue:this.props.comment}; 
    }

    render(){ 
        const {handleModal, handleSubmit, handleChange, replyId} = this.props;
        return(
            <div className="col-md-12">
                <form  method='post' encType="multipart/form-data" onSubmit={handleSubmit} >
                    <div className="form-group">
                        <label >message</label>
                        <imput name='name' className="form-control" />
                    </div>
                    <div className="form-group">
                        <label >message</label>
                        <textarea name="message"  className="form-control" rows="5" >
                        
                        </textarea>
                    </div>
                    <div className="form-group">
                        <label >conten-reply</label>
                        <textarea name="reply"  className="form-control" rows="5" onChange={handleChange} > 
                        </textarea>
                    </div>
                    <div className="btn-group">
                        <button type="submit" className="btn btn-primary" onClick={()=>setTimeout(()=>{handleModal(false)},800 )}>send</button>
                        <button type="button" onClick={()=>handleModal(false)}  className="btn btn-secondary">cancel</button>
                    </div>
                </form>
            </div>
        )
    }
}
