import React from 'react';
import {withRouter} from 'next/router';
import axios from '../../utils'; 
import ModalV from '../components/modal';
import CommentAdd from './commentAdd';
import CommentV from './commentV';

class Comment extends React.Component{
    constructor(props){
        super(props);
        this.state =  {
            modal:false,
            comments : [],
            modleShow:false,
            handleGetId: null,
            modal: false
        }
        this.handleModal = this.handleModal.bind(this);
        this.fetchComment = this.fetchComment.bind(this);
        this.handleDelete = this.handleDelete.bind(this);
        this.deleteConfirm = this.deleteConfirm.bind(this);
        this.handleRemove = this.handleRemove.bind(this);
        this.handleModal = this.handleModal.bind(this);
    }

    handleModal(param){
        let {modal} = this.state;
        modal = param;
        this.setState({modal});
    }
    fetchComment(){
        let {comments} = this.state;
        axios.get('/comment')
        .then(res =>{
            const {data} = res.data;
            comments = data;
            this.setState({comments});
            // console.log(comments,'=============>fetch cmo')
        })
    }
    deleteConfirm(param){
        let {modleShow} = this.state;
        modleShow = param; 
        this.setState({modleShow});
         
    }
    handleDelete(param){
        let {handleGetId} = this.state;
        handleGetId = param;
        this.setState({handleGetId});
    }
    handleRemove(param){
        const id = param; 
        this.deleteConfirm(true); 
        try{
            axios.delete(`/comment/delete/${id}`)
             .then(res => {
                if(res.data){
                    this.fetchComment();
                }
             })
         }catch(err){ 
             throw err 
         }
    }
    handleModal(param){
        let {modal} = this.state;
        modal = param;
        this.setState({modal});
    }
    handle(param){
        let {article} = this.state;
        article = param;
        this.setState({article});
    }
    componentDidMount(){ 
        this.fetchComment()
    }
    render(){ 
        const {modal, comments, modleShow, handleGetId} = this.state;
        return(
            <div className="row"> 
                <div className="col-md-12 text-right">
                    <button type="button" onClick={()=>{this.handleModal(true) }} className="btn btn-primary col-md-1">them</button>
                </div> 
                <CommentV
                    comments = {comments} deleteConfirm ={this.deleteConfirm}
                    handleDelete={this.handleDelete} handleModal = {this.handleModal}
                /> 
                <div className="col-md-12">
                    { modal ?
                    <CommentAdd /> : ''
                    }
                </div>
                <ModalV 
                    modleShow={modleShow} handleRemove ={this.handleRemove}
                    handleGetId={handleGetId} deleteConfirm ={this.deleteConfirm}
                    handleModal = {this.handleModal}
                />
            </div>
        )
    }
} 
export default withRouter(Comment)
