import './style.scss';
import React from 'react';
import {withRouter} from 'next/router';
import axios from 'axios';
import AddUser from './addArticle';
import UserV from './ArticleV';
import ModalV from '../components/modal';
class User extends React.Component{
    constructor(props){
        super(props);
        this.state = {form:{},
            modal:false,
            articles:[],
            article:null,
            modleShow:false,
            handleGetId: null,
            showSucess: false,
            sucess: null,
            search: null,
            params:{ page: 1, size: 20 },

        };
        
        this.handleModal = this.handleModal.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.fetchArticle = this.fetchArticle.bind(this);
        this.handleEdit = this.handleEdit.bind(this);
        this.handleRemove = this.handleRemove.bind(this);
        this.deleteConfirm = this.deleteConfirm.bind(this);
        this.handleGetIdArticle = this.handleGetIdArticle.bind(this);
        this.opentSucces = this.opentSucces.bind(this);
        this.handleSucces = this.handleSucces.bind(this);
        this.getsearch = this.getsearch.bind(this);
    }
    handleChange(even){
        let {form} = this.state;
        const name = even.target.name;
        const value = even.target.value; 
        form[name] = value;
        this.setState({ form }); 
    }
    getsearch(even){
        let {search} = this.state;
        const value = even.target.value; 
        search = value;
        this.setState({search}); 
        this.fetchArticle()
    }
    handleSubmit(event){
        event.preventDefault();
        let {form,article} = this.state; 
        console.log(article,'===========================??form')
        try{
            if(article._id){
                const id = article._id;
                axios.put(`http://localhost:3333/article/edit/${id}`,{form})
                .then(res=>{
                    let param = res.data.message;
                    if(res.data){
                        this.fetchArticle();
                        this.opentSucces(param);
                        this.handleSucces(true)
                    }
                })
            }else{
                axios.post('http://localhost:3333/article/add',{form})
                .then(res=>{ 
                    let param = res.data.message;
                    if(res.data){ 
                        this.fetchArticle();
                        this.opentSucces(param);
                        this.handleSucces(true)
                    }
                })
            }
        }catch(err){
            throw err
        }
    } 
    handleModal(param){
        let {modal} = this.state;
        modal = param;
        this.setState({modal});
    }
    //==================================>Email existed
    opentSucces(param){
        let {sucess} = this.state;
        sucess = param;
        this.setState({sucess}); 
    }
    handleSucces(param){
        let {showSucess} = this.state;
        showSucess = param;
        this.setState({showSucess}); 
        let close = false;
        setTimeout(()=>{
            showSucess = close;
            this.setState({showSucess}); 
        },2500)
    }
    //==================================>//
    fetchArticle(){
        let {articles,params,search} = this.state;
        // const {router} = this.props;
        // const params = router.query;
        // params = 
        console.log(search,'==========================>?router')
        try{
            axios.get('http://localhost:3333/article',{params:{search}})
             .then(res => { 
                if(res.data){
                    articles = res.data.data || []
                    this.setState({ articles }); 
                    return articles 
                }
             })
         }catch(err){ 
             throw err 
         }
    }
    
    handleEdit(param){
        let {article} = this.state;
        article = param;
        this.setState({article});
    }
    deleteConfirm(param){
        let {modleShow} = this.state;
        modleShow = param; 
        this.setState({modleShow});
         
    }
    handleGetIdArticle(param){
        let {handleGetId} = this.state;
        handleGetId = param;
        this.setState({handleGetId});
    }
    handleRemove(param){
        const id = param._id; 
        this.deleteConfirm(true); 
        try{
            axios.delete(`http://localhost:3333/article/delete/${id}`)
             .then(res => { 
                if(res.data){
                    this.fetchArticle();
                }
             })
         }catch(err){ 
             throw err 
         }
    }
    componentDidMount(){
        this.fetchArticle();
    }
    render(){
        const {articles, modal, article, modleShow, handleGetId,sucess,showSucess} = this.state; 
        return(
            <div className="row"> 
                <div className="col-md-12 text-right">
                    <button type="button" onClick={()=>{this.handleModal(true);this.handleEdit(false)}} className="btn btn-primary col-md-1">them</button>
                </div>
                { modal ? '' :
                    <div className="col-md-12 box-form"> 
                        <form >
                            <div className="form-row">
                                <div className="col-7">
                                    <input type="text"  className="form-control" placeholder="nhập từ cần tìm kiếm" onChange={this.getsearch} />
                                </div> 
                            </div>
                        </form>  
                    </div>
                }
                <UserV 
                    modal={modal} articles={articles}
                    handleModal={this.handleModal} deleteConfirm={this.deleteConfirm} handleEdit={this.handleEdit} handleGetIdArticle={this.handleGetIdArticle} 
                />
                 
                <ModalV
                    modleShow={modleShow} handleGetId={handleGetId} sucess={sucess} showSucess={showSucess} 
                    deleteConfirm={this.deleteConfirm} handleRemove ={this.handleRemove} handleSucces={this.handleSucces}
                  />
                <div className="col-md-12">
                    {modal && <AddUser
                        handleChange={this.handleChange}
                        handleSubmit={this.handleSubmit}
                        handleModal={this.handleModal}
                        fetchArticle = {this.fetchArticle}
                        article = {article}
                    />} 
                </div>
            </div>
        )
    }
} 
export default withRouter(User)
